intro.pre:
using Castle.ActiveRecord;

[ActiveRecord("blogs")]
public class Blog : ActiveRecordBase
{
	private int id;
	private string name;
	private IList posts = new ArrayList();
	
	[PrimaryKey]
	public int Id
	{
		get { return id; }
		set { id = value; }
	}

	[Property]
	public string Name
	{
		get { return name; }
		set { name = value; }
	}
	
	[HasMany(typeof(Post), Table="Posts", ColumnKey="blogid")]
	public IList Posts
	{
		get { return posts; }
		set { posts = value; }
	}
}

Blog blog = new Blog();
blog.Name = "hammett's blog";
blog.Create();

Post post = new Post();
post.Title = "First post";
post.Contents = "Hello world";
post.Create();

blog.Posts.Add(post);
blog.Update();

[HasMany(typeof(Post), Table="Posts", ColumnKey="blogid", Inverse=true)]
public IList Posts
{
	get { return posts; }
	set { posts = value; }
}
