[ActiveRecord]
public class Category : ActiveRecordBase
{
    private int id;
    private string name;
    private Category parent;
    private IList subcategories;

    [PrimaryKey]
    public int Id
    {
        get { return id; }
        set { id = value; }
    }

    [Property]
    public string Name
    {
        get { return name; }
        set { name = value; }
    }

    [BelongsTo("parent_id")]
    public Category Parent
    {
        get { return parent; }
        set { parent = value; }
    }

    [HasMany(typeof(Category))]
    public IList SubCategories
    {
        get { return subcategories; }
        set { subcategories = value; }
    }
	
    public static Category[] FindAll()
    {
        return (Category[]) ActiveRecordBase.FindAll(typeof(Category));
    }
}

[ActiveRecord]
public class Blog : ActiveRecordBase<Blog>
{
    private int id;
    private string name;
    private string author;

    [PrimaryKey]
    public int Id
    {
        get { return id; }
        set { id = value; }
    }

    [Property]
    public string Name
    {
        get { return name; }
        set { name = value; }
    }

    [Property]
    public string Author
    {
        get { return author; }
        set { author = value; }
    }
}
