[DefaultAction]
public class HomeController : Controller
{
	public void Index()
	{
	}

	public void DefaultAction()
	{
		string template = "notfound";

		if (HasTemplate("home/" + Action))
		{
			template = Action;
		}

		RenderView(template);
	}
}

[DefaultAction("Foo")]
public class HomeController : Controller
{
	public void Index()
	{
	}

	public void Foo()
	{
		RenderText(Action + " was not found");
	}
}
